import React, {useEffect, useRef, useState} from 'react';
import './SpecialOffer.css'
import Arrow from "../UI/Arrows/Arrow";
import SpecialOfferLine from "./SpecialOfferLine/SpecialOfferLine";
import {usePressingButton} from "../../hooks/usePressingButton";
import logo from "../Header/Logo/Logo";

const SpecialOffer = () => {
    const leftArrowRef = useRef()
    const rightArrowRef = useRef()
    const isLeftArrowPressing = usePressingButton(leftArrowRef)
    const isRightArrowPressing = usePressingButton(rightArrowRef)

    const areaOfVisibilityRef = useRef()
    const [specialOffers, setSpecialOffers] = useState([
        {
            id: 1,
            name: 'Сопа Ацтека',
            cuisine: 'MEXICAN',
            photo: 'https://www.sbs.com.au/food/sites/sbs.com.au.food/files/styles/full/public/SopaAzteca-02.jpg?itok=U6rl9iXY',
            description: 'Традиционный мексиканский суп, приготовленный из обжаренных кусочков кукурузной лепешки, погруженных в бульон из помидоров, чеснока, лука и перца чили де арбол и эпазоте. Подается с кусочками чили пасилья, чичарроном, авокадо, кубиками свежего сыра и сметаной.'
        },
        {
            id: 2,
            name: 'Пад Тай',
            cuisine: 'THAI',
            photo: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSY1GQzxtrljNeLH84_lO75n4OvqJ5f7YjhhQ&usqp=CAU',
            description: 'Классическое блюдо тайской кухни из обжаренной рисовой вермишели с овощами и ароматным соусом. Приготовленное на гриле, считалось уличной едой и в местных ресторанах Таиланда.'
        },
        {
            id: 3,
            name: 'Дал',
            cuisine: 'INDIAN',
            photo: 'data:image/jpeg;base64,',
            description: '   Традиционный веганский индийский пряный суп-пюре из разваренных бобовых. Часто для вкуса добавляют порошок карри, кокосовое молоко, лимонный сок, помидоры, чеснок и обжаренный лук. Дал традиционно подаётся с тёплыми лепёшками.'
        },
        {
            id: 4,
            name: 'Куриные крылышки Баффало',
            cuisine: 'AMERICAN',
            photo: 'data:image/jpeg;base64,',
            description: 'Традиционная закуска американской кухни. Куриные крылья жарят во фритюре и окунают в острый соус из уксуса, кайенского перца и расплавленного сливочного масла. Рецепт был создан в 1964 году Терезой Белиссимо, хозяйкой бара «Анкор» в городе Буффало, штат Нью-Йорк.'
        }
    ])
    const [currentOfferIndex, setCurrentOfferIndex] = useState(0)
    const [areaOfVisibilityWidth, setAreaOfVisiblyWidth] = useState(0)
    const [lineLength, setLineLength] = useState(0)
    const [linePosition, setLinePosition] = useState(0)

    useEffect(() => {
        const currentAreaOfVisiblyWidth = areaOfVisibilityRef.current.offsetWidth
        setLineLength(currentAreaOfVisiblyWidth * specialOffers.length)
        setAreaOfVisiblyWidth(currentAreaOfVisiblyWidth)
    }, [])

    useEffect(() => {
        if (isLeftArrowPressing && linePosition === 0) {
            setLinePosition(20)
        }
        if (!isLeftArrowPressing && linePosition === 20) {
            setLinePosition(0)
        }
        if (isRightArrowPressing && linePosition === areaOfVisibilityWidth - lineLength) {
            setLinePosition(areaOfVisibilityWidth - lineLength - 20)
        }
        if (!isRightArrowPressing && linePosition === (areaOfVisibilityWidth - lineLength - 20)) {
            setLinePosition(areaOfVisibilityWidth - lineLength)
        }
    }, [isLeftArrowPressing, isRightArrowPressing])

    function rightMotion() {
        if (linePosition > areaOfVisibilityWidth - lineLength) {
            setLinePosition(linePosition - areaOfVisibilityWidth)
            setCurrentOfferIndex(currentOfferIndex + 1)
        }
    }

    function leftMotion() {
        if (linePosition < 0) {
            setLinePosition(linePosition + areaOfVisibilityWidth)
            setCurrentOfferIndex(currentOfferIndex - 1)
        }
    }

    function tryCurrentOffer() {
        console.log(specialOffers[currentOfferIndex])
    }

    return (
        <div className="specialOffer">
            <div className="somethingNewText">ЧТО-ТО НОВОЕ</div>
            <div className="specialOfferCenter">
                <div className="arrowArea">
                    <Arrow
                        direction='left'
                        onClick={leftMotion}
                        ref={leftArrowRef}
                    />
                </div>
                <div className="areaOfVisibility" ref={areaOfVisibilityRef}>
                    {areaOfVisibilityWidth > 0 &&
                        <SpecialOfferLine
                            specialOffers={specialOffers}
                            itemWidth={areaOfVisibilityWidth}
                            lineLength={lineLength}
                            linePosition={linePosition}
                        />}
                </div>
                <div className="arrowArea right">
                    <Arrow
                        direction='right'
                        onClick={rightMotion}
                        ref={rightArrowRef}
                    />
                </div>
            </div>
            <div className="wantToTry" onClick={tryCurrentOffer}>ГДЕ ПОПРОБОВАТЬ?</div>
        </div>
    );
};

export default SpecialOffer;